import React, {useState} from 'react';
import Tweet from "./tweet";

function App () {
  const [isRed, setRed] = useState(false);
  const [count, setCount] = useState(0);


  const increment = () => {
    setCount(count+1);
    setRed(true);
  };

  return (
    <div className="app">
      <h1 className = { isRed ? "red": "" }>change colors</h1>
      <button onClick={increment}>Increment</button>
      <h1>{count}</h1>
    {/* <Tweet name="Ula" message="hello women!" />
